import React, { useState } from "react";
import Header from "./Header";


function App() {

  const numbers = ["7", "8", "9", "4", "5", "6", "1", "2", "3", "0"];
  const operators = ["=", "+", "-", "/", "*", ".", "AC"];

  const [lastPressed, setLastPressed] = useState(undefined);
  const [currentNumber, setCurrentNumber] = useState("0");
  const [prevNumber, setPrevNumber] = useState(undefined);
  const [operator, setOperator] = useState(false);

  const handleClick = (event) => {
    const { innerText } = event.target;
    console.log(currentNumber.length);
    let hello;



    /* if (!Number.isNaN(Number(innerText))) {
      if (currentNumber === "0")
        setCurrentNumber(innerText)
    } else {
      setCurrentNumber(currentNumber + innerText)
    } */

    switch (innerText) {
      case '1':
      case '2':
      case '3':
      case '4':
      case '5':
      case '6':
      case '7':
      case '8':
      case '9':
        if(currentNumber === "0") {
          setCurrentNumber(innerText);
        } else {
          setCurrentNumber(currentNumber + innerText);
        }
        // setCurrentNumber(currentNumber + innerText);
        setOperator(false);

        break;
      case "0":
        if (currentNumber.length === 1) {
          setCurrentNumber(innerText);
        } else {
          setCurrentNumber(currentNumber + innerText);
        }


        break;
      case "AC":
        setCurrentNumber("0");
        setPrevNumber(undefined);
        break;

      case ".":
        if (!currentNumber.includes(".")) {
          setCurrentNumber(currentNumber + innerText);
        }
        break;

      case '/':
      case '+':
      case '-':
      case '*':
        if (!operator) {
          setCurrentNumber(currentNumber + innerText);
          setOperator(true);
        }
        break;
    }
    setLastPressed(innerText);
  }

  return (
    <div>
      <Header />
      <div className="calculator">
        <div id="display" className="display">{currentNumber}</div>
        <div className="nums-container">
          {numbers.map((num, index) => {
            return (
              <button onClick={handleClick} key={num}>{num}</button>
            )
          })}
        </div>

        <div className="op-container">
          {operators.map((op, index) => {
            return (
              <button onClick={handleClick} key={op}>{op}</button>
            )
          })}
        </div>
        {/* <button>7</button>
        <button>8</button>
        <button>9</button>
        <button>%</button>

        <button>4</button>
        <button>5</button>
        <button>6</button>
        <button>X</button>

        <button>1</button>
        <button>2</button>
        <button>3</button>
        <button>--</button>

        <button>0</button>
        <button>..</button>
        <button>+</button>
        <button>=</button>

        <button>AC</button> */}
      </div>
    </div>
  );
}

export default App;
