import React, { useState } from "react";
import Header from "./Header";


function App() {

  const firstRow = ["7", "8", "9", "/"];

  const numbers = ["7", "8", "9", "4", "5", "6", "1", "2", "3", "0"];
  const operators = ["/", "*", "-", "=", "*", ".", "AC"];

  const [currentNumber, setCurrentNumber] = useState("0");
  const [operator, setOperator] = useState(false);
  const [secondOperator, setSecondOperator] = useState(false);
  const [nullOperator, setNullOperator] = useState(false);

  const handleClick = (event) => {
    const { innerText } = event.target;

    switch (innerText) {
      case '1':
      case '2':
      case '3':
      case '4':
      case '5':
      case '6':
      case '7':
      case '8':
      case '9':
        if (currentNumber === "0") {
          setCurrentNumber(innerText);
        } else if (nullOperator) {
          setCurrentNumber(currentNumber);
        } else {
          setCurrentNumber(currentNumber + innerText);
        }
        setOperator(false);
        break;

      case "0":
        if (currentNumber === "0") {
          setCurrentNumber(innerText);
        } else if (!nullOperator && operator) {
          setCurrentNumber(currentNumber + innerText);
          setNullOperator(true);


        } else if (nullOperator) {
          setCurrentNumber(currentNumber);
          setOperator(true);
        } else {
          setCurrentNumber(currentNumber + innerText);
        }
        break;

      case "AC":
        setCurrentNumber("0");
        setOperator(false);
        setSecondOperator(false);
        setNullOperator(false);
        break;

      case ".":
        if (!secondOperator) {
          setCurrentNumber(currentNumber + innerText);
          setSecondOperator(true);
        }
        setOperator(true);
        setNullOperator(false);
        break;

      case '/':
      case '+':
      case '-':
      case '*':
        if (!operator) {
          setCurrentNumber(currentNumber + innerText);
          setOperator(true);
        }
        setSecondOperator(false);
        setNullOperator(false)
        break;

      case "=":
        setCurrentNumber(eval(currentNumber));
        setSecondOperator(false);
        setOperator(false);
    }
  }

  return (
    <div>
      <Header />
      <div className="calculator">
        <div id="display" className="display">{currentNumber}</div>
        <div className="nums-container">
          {numbers.map((num, index) => {
            return (
              <button onClick={handleClick} key={num}>{num}</button>
            )
          })}
        </div>

        <div className="op-container">
          {operators.map((op, index) => {
            return (
              <button onClick={handleClick} key={op}>{op}</button>
            )
          })}
        </div>
        {/* <button>7</button>
        <button>8</button>
        <button>9</button>
        <button>%</button>

        <button>4</button>
        <button>5</button>
        <button>6</button>
        <button>X</button>

        <button>1</button>
        <button>2</button>
        <button>3</button>
        <button>--</button>

        <button>0</button>
        <button>..</button>
        <button>+</button>
        <button>=</button>

        <button>AC</button> */}
      </div>
    </div>
  );
}

export default App;
